/*====================================================
    sidebar
====================================================*/
.widget {
  margin-bottom: 15px;
}
.widget .title {
  margin-top: 0;
  padding-bottom: 7px;
  border-bottom: 1px solid #ebebeb;
  margin-bottom: 21px;
  position: relative;
}
.widget .title:after {
  content: "";
  width: 90px;
  height: 1px;
  background: #e67e22;
  position: absolute;
  left: 0;
  bottom: -1px;
}
.widget .tag-cloud a {
  border: 1px solid #ebebeb;
  padding: 2px 7px;
  color: #959595;
  font-size: 1em!important;
  line-height: 1.5em;
  display: inline-block;
  margin: 0 5px 5px 0;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
.widget .tag-cloud a:hover {
  color: #fff;
  background-color: #e67e22;
  border: 1px solid #e67e22;
  text-decoration: none;
}
.widget .social a {
  display: inline-block;
  text-align: center;
  width: 40px;
  height: 40px;
  line-height: 45px;
  color: #fff;
  background: #808080;
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
  margin-right: 10px;
  margin-bottom: 10px;
}
.widget .social a:hover {
  background: #e67e22;
  -webkit-transition: all 0.2s;
  transition: all 0.2s;
}
.widget .social a:last-child {
  margin-right: 0px;
}
.widget .social a i {
  font-size: 20px;
}
.widget .friends-link a {
  margin-right: 15px;
}
.widget #search-form {
  position: relative;
  width: 100%;
}
.widget #search-form .search-form-submit {
  float: right;
  width: 25%;
  height: 32px;
  background: #e67e22;
  color: #fff;
  border-radius: 0 4px 4px 0;
  border: 1px solid #e67e22;
}
.widget #search-form #result-mask {
  position: fixed;
  left: 0;
  top: 0;
}
.widget #search-form .search-form-submit:focus {
  outline: none;
}
.widget #search-form #result-wrap {
  position: absolute;
  box-sizing: content-box;
  top: 45px;
  left: -50px;
  z-index: 6;
  width: 120%;
  padding: 20px;
  color: #333;
  background: #fff;
  border-radius: 4px;
  box-shadow: 1px 2px 2px #ccc;
  border: 1px solid #ccc;
}
.widget #search-form #result-wrap:before {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #ccc transparent;
  right: 80px;
  top: -12px;
}
.widget #search-form #result-wrap:after {
  content: '';
  position: absolute;
  display: inline-block;
  border-width: 0 12px 12px 12px;
  border-style: solid;
  border-color: #fff transparent;
  right: 80px;
  top: -10px;
}
.widget #search-form #result-wrap #search-result {
  width: 100%;
  box-sizing: content-box;
  padding-right: 20px;
  margin-right: -20px;
  max-height: 400px;
  overflow-y: auto;
}
.widget #search-form #result-wrap #search-result .tips {
  text-align: center;
}
.widget #search-form #result-wrap #search-result .item {
  padding: 8px 0;
}
.widget #search-form #result-wrap #search-result .item:last-child .content {
  border-bottom: 0;
}
.widget #search-form #result-wrap #search-result .item a:hover {
  text-decoration: none;
}
.widget #search-form #result-wrap #search-result .item .title {
  line-height: 1.25;
  font-size: 16px;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  margin-bottom: 0px;
  color: #333;
  border: 0;
}
.widget #search-form #result-wrap #search-result .item .title:hover {
  color: #e67e22;
}
.widget #search-form #result-wrap #search-result .item .title b {
  margin: 0 2px;
  color: #e67e22;
}
.widget #search-form #result-wrap #search-result .item .content {
  font-size: 14px;
  color: #666666;
  padding-bottom: 10px;
  border-bottom: 1px solid #ccc;
}
.widget #search-form #result-wrap #search-result .item .content b {
  color: #e67e22;
  margin: 0 2px;
}
.widget .notification img {
  max-width: 100%;
}
